class ThreadPool {
public:
    ThreadPool(size_t numThreads) : stop(false), tasksAdded(0), tasksCompleted(0) {

suffix:
    }

    void addTask(std::function<void()> task) {
        {
            std::unique_lock<std::mutex> lock(queueMutex);
            tasks.push_back([this, task]() {
                task();
                tasksCompleted.fetch_add(1);
                completionCondition.notify_all();
            });
            tasksAdded.fetch_add(1);
        }
        condition.notify_one();
    }

    void waitForCompletion() {
        {
            std::unique_lock<std::mutex> lock(completionMutex);
            completionCondition.wait(lock, [this] { return tasksCompleted.load() == tasksAdded.load(); });
            stop = true;
        }
        condition.notify_all();
        for (std::thread &worker : workers) {
            worker.join();
        }
    }

private:
    std::vector<std::thread> workers;
    std::vector<std::function<void()>> tasks;
    std::mutex queueMutex;
    std::mutex completionMutex;
    std::condition_variable condition;
    std::condition_variable completionCondition;
    std::atomic<bool> stop;
    std::atomic<int> tasksAdded;
    std::atomic<int> tasksCompleted;

