    ThreadPool pool(4);
    std::atomic<int> counter(0);

    for (int i = 0; i < 10; ++i) {
        pool.addTask([&counter] { counter.fetch_add(1, std::memory_order_relaxed); });
    }

    pool.waitForCompletion();
    assert(counter == 10);
    return 0;
