// Additional functionality to watch for file changes and rebuild
type Watcher = {
	watchDir: string;
	startWatching: () => void;
	stopWatching: () => void;
};

const createWatcher = (watchDir: string): Watcher => {
	let watcher: fs.FSWatcher | null = null;

	const startWatching = () => {
		if (!watcher) {
			ensureDirExists(watchDir);
			const indexPath = path.join(watchDir, config.entryFile);
			if (!fs.existsSync(indexPath)) {
				fs.writeFileSync(indexPath, 'console.log("Hello World");');
			}
			watcher = fs.watch(watchDir, (eventType, filename) => {
				if (filename && eventType === 'change') {
					console.log(`File changed: ${filename}`);
					buildProject();
				}
			});
		}
	};

	const stopWatching = () => {
		if (watcher) {
			watcher.close();
			watcher = null;
		}
	};

	return { watchDir, startWatching, stopWatching };
};

